import { Container, Row, Col, Tab, Nav } from "react-bootstrap";
import { ProjectCard } from "./ProjectCard";
import farmc from "../assets/img/farmC.jpeg";
import blog from "../assets/img/blog.png";
import anime from "../assets/img/anime.png";
import colorSharp2 from "../assets/img/color-sharp2.png";
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Projects = () => {
  const projects = [
    {
      title: "Farm Connect App",
      description: "A platform connecting farmers directly to retailers, eliminating middlemen and enhancing profits.",
      imgUrl: farmc,
    },
    {
      title: "Blog App",
      description: "A full-stack application for publishing and managing blogs with user authentication.",
      imgUrl: blog,
    },
    {
      title: "User Management App",
      description: "A system for managing user data, roles, and profiles effectively.",
      // imgUrl: projImg3,
    },
    {
      title: "Anime Explorer",
      description: "A React app showcasing popular anime with detailed information and recommendations.",
      imgUrl: anime,
    },
  ];

  return (
    <section className="project" id="projects">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
                  <h2>Projects</h2>
                  <p>
                    Here are some of the key projects I've worked on, showcasing my expertise in
                    web development, app development, and data science. Each project is designed
                    to solve real-world problems and provide user-centric solutions.
                  </p>
                  <Tab.Container id="projects-tabs" defaultActiveKey="first">
                    <Nav
                      variant="pills"
                      className="nav-pills mb-5 justify-content-center align-items-center"
                      id="pills-tab"
                    >
                      <Nav.Item>
                        <Nav.Link eventKey="first" className="">
                          Web Development
                        </Nav.Link>
                      </Nav.Item>
                      {/* Uncomment if additional tabs are needed */}
                      {/* 
                      <Nav.Item>
                        <Nav.Link eventKey="second">App Development</Nav.Link>
                      </Nav.Item>
                      <Nav.Item>
                        <Nav.Link eventKey="third">Other Projects</Nav.Link>
                      </Nav.Item> 
                      */}
                    </Nav>
                    <Tab.Content
                      id="slideInUp"
                      className={isVisible ? "animate__animated animate__slideInUp" : ""}
                    >
                      <Tab.Pane eventKey="first">
                        <Row>
                          {projects.slice(0, 4).map((project, index) => (
                            <ProjectCard key={index} {...project} />
                          ))}
                        </Row>
                      </Tab.Pane>
                      {/* Uncomment if additional panes are needed */}
                      {/* 
                      <Tab.Pane eventKey="second">
                        <Row>
                          {projects.slice(3, 5).map((project, index) => (
                            <ProjectCard key={index} {...project} />
                          ))}
                        </Row>
                      </Tab.Pane>
                      <Tab.Pane eventKey="third">
                        <Row>
                          {projects.slice(5).map((project, index) => (
                            <ProjectCard key={index} {...project} />
                          ))}
                        </Row>
                      </Tab.Pane> 
                      */}
                    </Tab.Content>
                  </Tab.Container>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2} alt="Background" />
    </section>
  );
};
